export const initialState = {
  isLoggingIn: false, //로그인 시도중
  isLoggingOut: false,
  isLoggedIn: false,
  me: null,
  signUpData: {},
  loginData: {},
};

export const loginAction = data => {
  return (dispatch, getState) => {
    const state = getState();
    dispatch(loginRequestAction());
    axios
      .post('/api/login')
      .then(res => loginSuccessAction(res.data))
      .catch(error => loginFailureAction(error));
  };
};

export const loginRequestAction = data => {
  return {
    type: 'LOG_IN_REQUEST',
    data,
  };
};

export const logoutRequestAction = data => {
  return {
    type: 'LOG_OUT_REQUEST',
    data,
  };
};

export const logoutSuccessAction = data => {
  return {
    type: 'LOG_OUT_SUCCESS',
    data,
  };
};

export const logoutFailureAction = data => {
  return {
    type: 'LOG_OUT_FAILURE',
    data,
  };
};

export const changeNickname = () => {
  return {
    type: 'CHANGE_NICKNAME',
  };
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'LOG_IN_REQUEST':
      return {
        ...state,
        isLoggedIn: true,
        isLoggingIn: false,
        me: action.data,
      };
    case 'LOG_IN_SUCCESS':
      return {
        ...state,
        isLoggedIn: false,
        isLoggingIn: false,
        me: { ...action.data, nickname: ['zeromoon'] },
      };
    case 'LOG_IN_FAILURE':
      return {
        ...state,
        isLoggedIn: false,
        isLoggingIn: false,
      };
    case 'LOG_OUT_REQUEST':
      return {
        ...state,
        isLoggingOut: true,
      };
    case 'LOG_OUT_SUCCESSS':
      return {
        ...state,
        isLoggingOut: false,
        isLoggedIn: false,
        me: null,
      };
    case 'LOG_OUT_FAILURE':
      return {
        ...state,
        isLoggingOut: false,
      };
    default:
      return state;
  }
};

export default reducer;
