export const initialState = {
  mainPosts: [
    {
      id: 1,
      User: {
        id: 1,
        nickname: '제로문',
      },
      content: '첫 번째 게시글 #해시태그 #익스프레스',
      Images: [],
      Conments: [
        {
          User: {
            nickname: 'moon',
          },
          content: '우와 게정판이 나왔군요~',
        },
        {
          User: {
            nickname: ' HAUL',
          },
          content: '멍멍멍~',
        },
      ],
    },
  ],
  imagePaths: [],
  postAdded: false,
};

const ADD_POST = 'ADD_POST';
export const addPost = {
  type: ADD_POST,
};

const dummyPost = {
  id: 2,
  content: '젤다의 전설, 꿈꾸는 섬!',
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_POST:
      return {
        ...state,
        mainPosts: [dummyPost, ...state.mainPosts],
        postAdded: true,
      };
    default:
      return state;
  }
};

export default reducer;
